// AnimatedSection.js
import React, { useEffect } from "react";
import classes from "./AnimatedSVG.module.css";
import GitHubIcon from "../Icons/GitHubIcon";
import LinkedinIcon from "../Icons/Linkedin";
import PdfIcon from "../Icons/PdfIcon";

const AnimatedSection = () => {
  useEffect(() => {
    const createSVGElement = (direction) => {
      const svg = document.createElementNS("http://www.w3.org/2000/svg", "svg");
      svg.setAttribute("width", "50");
      svg.setAttribute("height", "50");
      svg.innerHTML = `<circle cx="25" cy="25" r="20" fill="blue" />`;

      if (direction === "top") {
        svg.style.transform = "translateY(-100%)";
      } else if (direction === "bottom") {
        svg.style.transform = "translateY(100%)";
      } else if (direction === "left") {
        svg.style.transform = "translateX(-100%)";
      } else if (direction === "right") {
        svg.style.transform = "translateX(100%)";
      }

      return svg;
    };

    const handleIntersection = (entries, observer) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          const svgTop = createSVGElement("top");
          const svgBottom = createSVGElement("bottom");
          const svgLeft = createSVGElement("left");
          const svgRight = createSVGElement("right");

          entry.target.appendChild(svgTop);
          entry.target.appendChild(svgBottom);
          entry.target.appendChild(svgLeft);
          entry.target.appendChild(svgRight);

          entry.target.querySelectorAll("svg").forEach((svg) => {
            svg.style.transition = "transform 1s ease-out";
            svg.style.transform = "translate(0, 0)";
          });

          observer.unobserve(entry.target);
        }
      });
    };

    const animatedSection = document.getElementById("animatedSection");
    const observer = new IntersectionObserver(handleIntersection, {
      threshold: 0.5,
    });

    observer.observe(animatedSection);

    // Clean up the observer when the component unmounts
    return () => observer.disconnect();
  }, []);

  return (
    <section id="animatedSection" className={classes.animatedSection}>
      <GitHubIcon />
      <LinkedinIcon />
      <PdfIcon />
    </section>
  );
};

export default AnimatedSection;
