import React, { useEffect, useState } from "react";
import "./AnimatedSection.css";

const AnimatedSection = () => {
  const [animate, setAnimate] = useState(false);

  useEffect(() => {
    const handleIntersection = (entries, observer) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          setAnimate(true);
          observer.unobserve(entry.target);
        }
      });
    };

    const animatedSection = document.getElementById("animatedSection");
    const observer = new IntersectionObserver(handleIntersection, {
      threshold: 0.5,
    });

    observer.observe(animatedSection);

    const handleResize = () => {
      // Re-observe the section when the window is resized
      observer.observe(animatedSection);
    };

    // Attach the event listener for window resize
    window.addEventListener("resize", handleResize);

    // Clean up the observer and event listener when the component unmounts
    return () => {
      observer.disconnect();
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  return (
    <section
      id="animatedSection"
      className={`animated-section ${animate ? "animate" : ""}`}
    >
      <div className="svg-container">
        <svg className="top">
          <circle cx="25" cy="25" r="20" fill="blue" />
        </svg>
        <svg className="bottom">
          <circle cx="25" cy="25" r="20" fill="blue" />
        </svg>
        <svg className="left">
          <circle cx="25" cy="25" r="20" fill="blue" />
        </svg>
        <svg className="right">
          <circle cx="25" cy="25" r="20" fill="blue" />
        </svg>
      </div>
    </section>
  );
};

export default AnimatedSection;
