import React, { useRef, useEffect, useState } from "react";
import "./AnimatedSVG.css"; // Import your CSS file for styling
import slika from "../Image/slikaCV.jpg";
import PdfIcon from "../Icons/PdfIcon";
import IconAngular from "../Icons/IconAngular";
import IconReact from "../Icons/IconReact";
import IconMicrosoftsqlserver from "../Icons/IconMicrosoftsqlserver";
import IconBxlJavascript from "../Icons/IconBxlJavascript";
import IconJava from "../Icons/IconJava";
import IconTypescript from "../Icons/IconTypescript";
import IconDotnet from "../Icons/IconDotnet";
import IconGit from "../Icons/IconGit";
const AnimatedSVG = () => {
  const imageRef = useRef(null);
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver((entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          setIsVisible(true);
          observer.unobserve(entry.target);
        }
      });
    });

    // Start observing the target element
    observer.observe(imageRef.current);

    // Cleanup the observer on component unmount
    return () => observer.disconnect();
  }, []); // Run this effect only once on component mount

  return (
    <div className={"main"}>
      <div className={"left"}>
        <div className={`image-section ${isVisible ? "visible" : ""}`}>
          <div ref={imageRef} className={"sl"}>
            <p>
              I have skills in following technologies: C++ and C#, Angular and
              .NET Java, React, Microsoft SQL.
            </p>
            <br />
            <p>
              I also have experience in using git for version control and GitHub
              and AzureDevOps for SDLC and Trello for managing tasks.
            </p>
          </div>
        </div>
      </div>
      <div className={"right"}>
        <div className={`svg-section ${isVisible ? "visible" : ""}`}>
          <div ref={imageRef} className={"sl"}>
            <IconAngular />
            <IconReact />
            <IconMicrosoftsqlserver />
            <IconBxlJavascript />
            <IconJava />
            <IconTypescript />
            <IconGit />
            <IconDotnet />
          </div>
        </div>
      </div>
    </div>
  );
};

export default AnimatedSVG;
