import React, { useRef, useEffect, useState } from "react";
import "./AnimatedSVG.css"; // Import your CSS file for styling
import slika from "../Image/slikaCV.jpg";
import PdfIcon from "../Icons/PdfIcon";
const AnimatedSVG = () => {
  const imageRef = useRef(null);
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver((entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          setIsVisible(true);
          observer.unobserve(entry.target);
        }
      });
    });

    // Start observing the target element
    observer.observe(imageRef.current);

    // Cleanup the observer on component unmount
    return () => observer.disconnect();
  }, []); // Run this effect only once on component mount

  return (
    <div className={"main"}>
      <div className={"right"}>
        <div className={`image-section ${isVisible ? "visible" : ""}`}>
          <div ref={imageRef} className={"sl"}>
            <PdfIcon />
          </div>
        </div>
      </div>
      <div className={`svg-section ${isVisible ? "visible" : ""}`}>
        <div ref={imageRef} className={"sl"}>
          <PdfIcon />
        </div>
      </div>
    </div>
  );
};

export default AnimatedSVG;
