// AnimatedSVG.js
import React, { useEffect, useRef } from "react";
import styled, { keyframes } from "styled-components";

const appearAnimation = keyframes`
  from {
    transform: translate(-100%, -100%);
    opacity: 0;
  }
  to {
    transform: translate(0, 0);
    opacity: 1;
  }
`;

const StyledSVG = styled.svg`
  opacity: 0; /* Initially hide the SVG */
  animation: ${(props) => (props.isVisible ? appearAnimation : "none")} 1s
    ease-out;
`;

const AnimatedSVG = () => {
  const svgRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            // When the element is in the viewport, set the isVisible state to true
            svgRef.current.style.opacity = 1;
          }
        });
      },
      { threshold: 0.5 } // Adjust the threshold based on your needs
    );

    if (svgRef.current) {
      observer.observe(svgRef.current);
    }

    return () => {
      if (svgRef.current) {
        observer.unobserve(svgRef.current);
      }
    };
  }, []);

  return (
    <StyledSVG
      ref={svgRef}
      isVisible={svgRef.current && svgRef.current.style.opacity === "1"}
      xmlns="http://www.w3.org/2000/svg"
      viewBox="0 0 24 24"
      width="50"
      height="50"
    >
      {/* Your SVG content goes here */}
      <circle cx="12" cy="12" r="10" fill="blue" />
    </StyledSVG>
  );
};

export default AnimatedSVG;
